INSERT INTO "classes" ("id","course_title","instructor_name","target_group","age_group","course_fee","course_date","frequency","location","image","created_at","content") VALUES (1,'Youth 9-14 fun Event','Blossoming Trees','Boys','9-12','0','2025-02-23 14:30:00.000000','Every Sunday','Basement','blossomingtry.png','2025-02-22 15:22:46.221879','<p><span class="ql-size-large" style="color: rgb(0, 0, 0);">You are receiving this poll because you had expressed interest in supporting the plan to host a youth fun event for younger age youth 9-14. We need to make a final decision on the time and date based on majority available for the upcoming event. You and at least 2 other youth leaders will be managing and leading the activities which were shared with you via email. Let''s decide the best time and day to host this first session event. Time is running out</span></p><p><br></p>');
INSERT INTO "classes" ("id","course_title","instructor_name","target_group","age_group","course_fee","course_date","frequency","location","image","created_at","content") VALUES (2,'Sunday school','Sheikh Moadh','Adults','9-12','20','2025-02-23 10:30:00.000000','Every Sunday','Mosque Main Hall','sunday_class.jpg','2025-02-22 23:41:46.046886','<p><strong style="color: rgb(64, 64, 64);">Welcoming Message:</strong></p><p><span style="color: rgb(64, 64, 64);">Bismillah ar-Rahman ar-Rahim! Welcome to our Sunday School family! Our goal is to nurture love for Allah, the Quran, and Islamic values in a joyful, supportive environment. Let’s learn, play, and grow together!</span></p><p><br></p><h3><strong>Program Structure</strong></h3><h4><strong>1. Age Group: Below 5 Years</strong></h4><p><strong>Session Name:</strong>&nbsp;<em>Quran Explorers</em></p><p><strong>Time:</strong>&nbsp;9:00 AM – 10:00 AM</p><ul><li><strong>Interactive Storytime:</strong>&nbsp;Simple Quranic stories (e.g., Prophet Adam, animals in the Quran) using puppets/visuals.</li><li><strong>Arabic Basics:</strong>&nbsp;Learn Arabic letters and short Islamic phrases (Assalamu Alaikum, Alhamdulillah) through songs.</li><li><strong>Creative Activity:</strong>&nbsp;Coloring pages of Arabic letters or Quranic themes.</li><li><strong>Playground Time:</strong>&nbsp;Supervised play to build social skills (10:00 AM – 10:15 AM).</li></ul><h4><strong>2. Age Group: 6–9 Years (Intermediate)</strong></h4><p><strong>Session Name:</strong>&nbsp;<em>Young Muslims Club</em></p><p><strong>Time:</strong>&nbsp;10:30 AM – 12:00 PM</p><ul><li><strong>Quranic Studies:</strong></li><li class="ql-indent-1">Short Surahs (Al-Fatiha, Al-Ikhlas) with tajweed basics.</li><li class="ql-indent-1">Understanding simple Quranic words and morals.</li><li><strong>Arabic Learning:</strong></li><li class="ql-indent-1">Writing Arabic letters and forming basic words (e.g., سلام, كتاب).</li><li><strong>Al-Adab &amp; Salah Workshop:</strong></li><li class="ql-indent-1"><strong>Al-Adab (Islamic Etiquette):</strong>&nbsp;Role-playing kindness, honesty, and respect.</li><li class="ql-indent-1"><strong>Salah Practice:</strong>&nbsp;Step-by-step wudu and prayer positions.</li><li><strong>Quran Memorization (Hifz):</strong>&nbsp;After Dhuhr (1:00 PM – 1:30 PM).</li></ul><h4><strong>3. Age Group: 9+ Years (Advanced)</strong></h4><p><strong>Session Name:</strong>&nbsp;<em>Quran Champions</em></p><p><strong>Time:</strong>&nbsp;10:30 AM – 12:30 PM</p><ul><li><strong>Quranic Studies:</strong></li><li class="ql-indent-1">Tajweed rules and deeper reflection on Surahs (e.g., Al-Kahf, Yasin).</li><li class="ql-indent-1">Tafsir (simplified) of selected verses.</li><li><strong>Arabic Mastery:</strong></li><li class="ql-indent-1">Grammar basics, forming sentences, and reading short Quranic verses.</li><li><strong>Al-Adab &amp; Islamic Lectures:</strong></li><li class="ql-indent-1">Discussions on ethics (honoring parents, community duties).</li><li class="ql-indent-1">Salah deep dive: Importance of khushu’ (focus) and Sunnah prayers.</li><li><strong>Quran Memorization (Hifz):</strong>&nbsp;After Dhuhr (1:00 PM – 2:00 PM).</li></ul><h3><strong>Additional Activities</strong></h3><ul><li><strong>Playground Time:</strong></li><li>Supervised outdoor play for all ages during breaks (10:15–10:30 AM, 12:00–12:15 PM).</li><li><strong>Lunch &amp; Dhuhr Prayer:</strong></li><li>Community lunch (12:15 PM – 1:00 PM) followed by group prayer.</li><li><strong>Closing Circle:</strong></li><li>Recap lessons, dua practice, and reminders for the week (2:00 PM – 2:15 PM).</li></ul><p><strong>Notes for Parents:</strong></p><ul><li>Bring a Quran, notebook, and water bottle.</li><li>Quran memorization books provided for Hifz students.</li><li>Volunteers will supervise playground activities.</li></ul><p><strong>End of Day:</strong></p><p>"Jazakum Allahu Khayran for joining us! Let’s carry today’s lessons into our week. See you next Sunday, InshaAllah!"</p><p>✨&nbsp;<em>Together we grow in faith, knowledge, and kindness!</em>&nbsp;✨</p>');
